"use strict";function alertModalControl(message,duration){document.getElementById("alertshader").style.display="block",document.getElementById("alertmessage").innerText=message,window.sleep(duration).then(()=>{document.getElementById("alertshader").style.display="none"})}function randomIntFromInterval(min,max){return Math.floor(Math.random()*(max-min+1)+min)}function sleep(ms){return new Promise(resolve=>setTimeout(resolve,ms))}function setCookie(cname,cvalue,exdays){var d=new Date;d.setTime(d.getTime()+24*exdays*60*60*1e3);var expires="expires="+d.toUTCString();document.cookie=cname+"="+cvalue+";"+expires+";path=/"}function getCookie(cname){for(var name=cname+"=",decodedCookie,ca=decodeURIComponent(document.cookie).split(";"),i=0;i<ca.length;i++){for(var c=ca[i];" "==c.charAt(0);)c=c.substring(1);if(0==c.indexOf(name))return c.substring(name.length,c.length)}return""}function addEventListenerById(id,event,handler){document.getElementById(id).addEventListener(event,handler)}function removeEventListenerById(id,event,handler){document.getElementById(id).removeEventListener(event,handler)}function removeClassFromElementById(id,cls){document.getElementById(id).classList.remove(cls)}function addClassFromElementById(id,cls){document.getElementById(id).classList.add(cls)}